import { Component, EventEmitter, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent implements OnInit {
  @Output() public emmitSearched: any = new EventEmitter();

  ngOnInit(): void {
 
  }

  public searchCharacter(value: string){
    this.emmitSearched.emit(value);
  }
}

<div class="responsive">
  <span class="title">Nome do Personagem</span>
  <form>
    <input type="text" #input (keyup)="searchCharacter(input.value)" placeholder="Digite o nome...">
    <img src="assets/icons/search-icon.svg" alt="Buscar Heroi da Marve">
  </form>
</div>
