import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

}
<header>
  <div class="header__title">
    <h1><u>BUS</u>CA MARVEL <span class="text_light">TESTE FRONTEND</span></h1> 
  </div>
<div class="header__name hide"> 
  <h1 class="text_light">UESLEI MARCELINO</h1>
</div>
</header>
